import React, { useContext } from 'react';
import { NavLink } from 'react-router-dom';
import { LanguageContext } from '../App';
import { translations } from '../utils/translations';

// AI-optimized: Fixed header with neon glow effects
// Mobile-first responsive navigation
function Header() {
  const { language, toggleLanguage } = useContext(LanguageContext);
  const t = translations.nav;

  return (
    <header className="header" role="banner">
      <div className="header-container">
        {/* Logo with neon glow */}
        <div className="logo" aria-label="Himalaya Apex Academy Logo">
          <span className="logo-text">HAA</span>
          <div className="logo-glow"></div>
        </div>

        {/* Navigation Links */}
        <nav className="nav-menu" role="navigation" aria-label="Main navigation">
          <NavLink 
            to="/home" 
            className={({ isActive }) => isActive ? 'nav-link active' : 'nav-link'}
            aria-label={t.home[language]}
          >
            {t.home[language]}
          </NavLink>

          <NavLink 
            to="/admissions" 
            className={({ isActive }) => isActive ? 'nav-link active' : 'nav-link'}
            aria-label={t.admissions[language]}
          >
            {t.admissions[language]}
          </NavLink>

          <NavLink 
            to="/gallery" 
            className={({ isActive }) => isActive ? 'nav-link active' : 'nav-link'}
            aria-label={t.gallery[language]}
          >
            {t.gallery[language]}
          </NavLink>
        </nav>

        {/* Language Toggle Button with AI-suggested UX */}
        <button 
          className="lang-toggle"
          onClick={toggleLanguage}
          aria-label={`Switch to ${language === 'en' ? 'Nepali' : 'English'}`}
          title="Toggle Language"
        >
          <span className="lang-icon">üåê</span>
          <span className="lang-code">{language === 'en' ? 'EN' : '‡§®‡•á‡§™‡§æ‡§≤‡•Ä'}</span>
        </button>
      </div>
    </header>
  );
}

export default Header;
