import React, { useContext, useState } from 'react';
import { LanguageContext } from '../App';
import { translations } from '../utils/translations';

// AI-optimized: Form validation with tooltips for rural users
function Admissions() {
  const { language } = useContext(LanguageContext);
  const t = translations.admissions;

  // Form state management
  const [formData, setFormData] = useState({
    studentName: '',
    parentEmail: '',
    selectedClass: '',
  });

  const [errors, setErrors] = useState({});
  const [submitted, setSubmitted] = useState(false);

  // AI-suggested validation for Nepali users
  const validateForm = () => {
    const newErrors = {};

    if (!formData.studentName.trim()) {
      newErrors.studentName = language === 'en' 
        ? 'Student name is required' 
        : 'विद्यार्थीको नाम आवश्यक छ';
    }

    // Email validation with helpful tooltip
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!formData.parentEmail.trim()) {
      newErrors.parentEmail = language === 'en' 
        ? 'Parent email is required' 
        : 'अभिभावकको इमेल आवश्यक छ';
    } else if (!emailRegex.test(formData.parentEmail)) {
      newErrors.parentEmail = language === 'en' 
        ? 'Enter a valid email (e.g., name@gmail.com)' 
        : 'मान्य इमेल प्रविष्ट गर्नुहोस् (उदा., name@gmail.com)';
    }

    if (!formData.selectedClass) {
      newErrors.selectedClass = language === 'en' 
        ? 'Please select a class' 
        : 'कृपया कक्षा चयन गर्नुहोस्';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      // AI-optimized: Simulate form submission (no backend needed)
      console.log('Form submitted:', formData);
      setSubmitted(true);

      // Reset form after 3 seconds
      setTimeout(() => {
        setFormData({ studentName: '', parentEmail: '', selectedClass: '' });
        setSubmitted(false);
      }, 3000);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  return (
    <div className="page-container admissions-page">
      {/* Header Section */}
      <section className="page-header">
        <h1 className="page-title">{t.title[language]}</h1>
        <p className="page-subtitle">{t.subtitle[language]}</p>
      </section>

      <div className="container">
        <div className="admissions-layout">
          {/* Application Form */}
          <section className="form-section" aria-labelledby="form-title">
            <h2 id="form-title" className="section-title">{t.formTitle[language]}</h2>

            {submitted && (
              <div className="success-message" role="alert">
                ✓ {t.successMsg[language]}
              </div>
            )}

            <form className="admission-form" onSubmit={handleSubmit} noValidate>
              {/* Student Name Field */}
              <div className="form-group">
                <label htmlFor="studentName" className="form-label">
                  {t.nameLabel[language]} <span className="required">*</span>
                </label>
                <input
                  type="text"
                  id="studentName"
                  name="studentName"
                  className={`form-input ${errors.studentName ? 'error' : ''}`}
                  value={formData.studentName}
                  onChange={handleChange}
                  placeholder={language === 'en' ? 'Enter full name' : 'पूरा नाम प्रविष्ट गर्नुहोस्'}
                  aria-required="true"
                  aria-invalid={!!errors.studentName}
                  aria-describedby={errors.studentName ? 'name-error' : undefined}
                />
                {errors.studentName && (
                  <span id="name-error" className="error-text" role="alert">
                    {errors.studentName}
                  </span>
                )}
              </div>

              {/* Parent Email Field (AI-suggested field) */}
              <div className="form-group">
                <label htmlFor="parentEmail" className="form-label">
                  {t.emailLabel[language]} <span className="required">*</span>
                  <span className="ai-badge" title="AI-suggested for easy contact">AI</span>
                </label>
                <input
                  type="email"
                  id="parentEmail"
                  name="parentEmail"
                  className={`form-input ${errors.parentEmail ? 'error' : ''}`}
                  value={formData.parentEmail}
                  onChange={handleChange}
                  placeholder={language === 'en' ? 'parent@gmail.com' : 'अभिभावक@gmail.com'}
                  aria-required="true"
                  aria-invalid={!!errors.parentEmail}
                  aria-describedby={errors.parentEmail ? 'email-error' : undefined}
                />
                {errors.parentEmail && (
                  <span id="email-error" className="error-text" role="alert">
                    {errors.parentEmail}
                  </span>
                )}
              </div>

              {/* Class Selection */}
              <div className="form-group">
                <label htmlFor="selectedClass" className="form-label">
                  {t.classLabel[language]} <span className="required">*</span>
                </label>
                <select
                  id="selectedClass"
                  name="selectedClass"
                  className={`form-input ${errors.selectedClass ? 'error' : ''}`}
                  value={formData.selectedClass}
                  onChange={handleChange}
                  aria-required="true"
                  aria-invalid={!!errors.selectedClass}
                  aria-describedby={errors.selectedClass ? 'class-error' : undefined}
                >
                  <option value="">{t.classPlaceholder[language]}</option>
                  {[...Array(12)].map((_, i) => (
                    <option key={i + 1} value={`Class ${i + 1}`}>
                      {language === 'en' ? `Class ${i + 1}` : `कक्षा ${i + 1}`}
                    </option>
                  ))}
                </select>
                {errors.selectedClass && (
                  <span id="class-error" className="error-text" role="alert">
                    {errors.selectedClass}
                  </span>
                )}
              </div>

              {/* Submit Button with Neon Glow */}
              <button type="submit" className="btn btn-primary btn-submit">
                {t.submitBtn[language]}
              </button>
            </form>
          </section>

          {/* Eligibility Table */}
          <section className="eligibility-section" aria-labelledby="eligibility-title">
            <h2 id="eligibility-title" className="section-title">
              {t.eligibilityTitle[language]}
            </h2>

            <div className="table-wrapper">
              <table className="eligibility-table" role="table">
                <thead>
                  <tr>
                    <th>{t.grade[language]}</th>
                    <th>{t.ageRequirement[language]}</th>
                    <th>{t.documents[language]}</th>
                  </tr>
                </thead>
                <tbody>
                  {translations.eligibilityData.map((row, index) => (
                    <tr key={index}>
                      <td>{row.grade[language]}</td>
                      <td>{row.age[language]}</td>
                      <td>{row.docs[language]}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </section>
        </div>
      </div>
    </div>
  );
}

export default Admissions;
