import React, { useState, useEffect, createContext } from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import './App.css';
import './components.css';
import './animations.css';

// Components
import Header from './components/Header';
import Home from './components/Home';
import Admissions from './components/Admissions';
import Gallery from './components/Gallery';
import Footer from './components/Footer';
import LoadingSpinner from './components/LoadingSpinner';

// AI-optimized: Create language context to avoid prop drilling
// Lightweight state management for bilingual support
export const LanguageContext = createContext();

function App() {
  // Initialize language from localStorage (persists user preference)
  const [language, setLanguage] = useState(() => {
    return localStorage.getItem('haa-language') || 'en';
  });

  // AI-optimized: Simulate initial loading for low-bandwidth users
  // Shows neural network animation while app hydrates
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Persist language choice to localStorage
    localStorage.setItem('haa-language', language);
  }, [language]);

  useEffect(() => {
    // Simulate app initialization (1.5 seconds for smooth UX)
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 1500);

    return () => clearTimeout(timer);
  }, []);

  // Toggle between English and Nepali
  const toggleLanguage = () => {
    setLanguage(prev => prev === 'en' ? 'np' : 'en');
  };

  if (isLoading) {
    return <LoadingSpinner />;
  }

  return (
    <LanguageContext.Provider value={{ language, toggleLanguage }}>
      <BrowserRouter>
        <div className="App">
          <Header />

          {/* Main content with fade-in animation */}
          <main className="main-content">
            <Routes>
              <Route path="/" element={<Navigate to="/home" replace />} />
              <Route path="/home" element={<Home />} />
              <Route path="/admissions" element={<Admissions />} />
              <Route path="/gallery" element={<Gallery />} />
              <Route path="*" element={<Navigate to="/home" replace />} />
            </Routes>
          </main>

          <Footer />
        </div>
      </BrowserRouter>
    </LanguageContext.Provider>
  );
}

export default App;
