import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';

// Fixed header with neon glow effects
// Mobile-first responsive navigation with hamburger menu
function Header() {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const closeMobileMenu = () => {
    setIsMobileMenuOpen(false);
  };

  // Handle body scroll lock when mobile menu is open
  useEffect(() => {
    if (isMobileMenuOpen) {
      document.body.classList.add('menu-open');
    } else {
      document.body.classList.remove('menu-open');
    }

    // Cleanup on unmount
    return () => {
      document.body.classList.remove('menu-open');
    };
  }, [isMobileMenuOpen]);

  // Close mobile menu on window resize
  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth > 768 && isMobileMenuOpen) {
        setIsMobileMenuOpen(false);
      }
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, [isMobileMenuOpen]);

  return (
    <>
      {/* Skip link for accessibility */}
      <a href="#main-content" className="skip-link">
        Skip to main content
      </a>

      <header className="header" role="banner">
        <div className="header-container">
          {/* Logo and school name */}
          <div className="logo-section">
            <div className="logo" aria-label="Ranabir Janahit Secondary School Logo">
              <img src="/logo.svg" alt="School Logo" className="logo-img" />
            </div>
            <div className="school-name">
              <span className="school-est-badge">ðŸŽ“ Est. 2020 B.S.</span>
              <span className="school-title">Ranabir Janahit Secondary School</span>
              <span className="school-tagline">Excellence in Education</span>
            </div>
          </div>

          {/* Hamburger Menu Button (Mobile Only) */}
          <button 
            className={`hamburger ${isMobileMenuOpen ? 'active' : ''}`}
            onClick={toggleMobileMenu}
            aria-label="Toggle navigation menu"
            aria-expanded={isMobileMenuOpen}
          >
            <span className="hamburger-line"></span>
            <span className="hamburger-line"></span>
            <span className="hamburger-line"></span>
          </button>

          {/* Navigation Links */}
          <nav className={`nav-menu ${isMobileMenuOpen ? 'mobile-open' : ''}`} role="navigation" aria-label="Main navigation">
            <NavLink 
              to="/home" 
              className={({ isActive }) => isActive ? 'nav-link active' : 'nav-link'}
              aria-label="Home"
              onClick={closeMobileMenu}
            >
              Home
            </NavLink>

            <NavLink 
              to="/about" 
              className={({ isActive }) => isActive ? 'nav-link active' : 'nav-link'}
              aria-label="About"
              onClick={closeMobileMenu}
            >
              About
            </NavLink>

            <NavLink 
              to="/admissions" 
              className={({ isActive }) => isActive ? 'nav-link active' : 'nav-link'}
              aria-label="Admissions"
              onClick={closeMobileMenu}
            >
              Admissions
            </NavLink>

            <NavLink 
              to="/gallery" 
              className={({ isActive }) => isActive ? 'nav-link active' : 'nav-link'}
              aria-label="Gallery"
              onClick={closeMobileMenu}
            >
              Gallery
            </NavLink>

            <NavLink 
              to="/contact" 
              className={({ isActive }) => isActive ? 'nav-link active' : 'nav-link'}
              aria-label="Contact"
              onClick={closeMobileMenu}
            >
              Contact
            </NavLink>
          </nav>
        </div>
      </header>

      {/* Mobile menu overlay */}
      <div 
        className={`nav-overlay ${isMobileMenuOpen ? 'active' : ''}`}
        onClick={closeMobileMenu}
        aria-hidden="true"
      ></div>
    </>
  );
}

export default Header;
